// Telegram getUpdatesã®ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚’å‡¦ç†
const response = $input.first().json;

if (!response.ok || !response.result || response.result.length === 0) {
  return [];
}

const updates = response.result;
const processedItems = [];
const allowedChatId = -4796493812;

for (const update of updates) {
  const message = update.message;

  if (message && message.document) {
    const chatId = message.chat.id;

    if (chatId === allowedChatId) {
      const document = message.document;
      const fileName = document.file_name;
      const fileExtension = fileName.split('.').pop().toLowerCase();

      if (['xlsx', 'xls', 'pdf'].includes(fileExtension)) {
        // ãƒ•ã‚¡ã‚¤ãƒ«åã‹ã‚‰æ—¥ä»˜ã‚’æŠ½å‡º
        const dateMatch = fileName.match(/(\d{4})[-._ ]?(\d{2})[-._ ]?(\d{2})/);
        let reportDate;
        if (dateMatch) {
          reportDate = `${dateMatch[1]}-${dateMatch[2]}-${dateMatch[3]}`;
        } else {
          reportDate = new Date().toISOString().split('T')[0];
        }

        processedItems.push({
          json: {
            update_id: update.update_id,
            message_id: message.message_id,
            chat_id: chatId,
            chat_name: message.chat.title,
            file_id: document.file_id,
            file_name: fileName,
            file_type: fileExtension,
            file_size: document.file_size,
            mime_type: document.mime_type,
            from_user: message.from.first_name || message.from.username,
            from_user_id: message.from.id,
            from_username: message.from.username || '',
            report_date: reportDate,
            received_at: new Date(message.date * 1000).toISOString()
          }
        });
      }
    }
  }
}

// offsetã‚’æ›´æ–°ï¼ˆæ¬¡å›å®Ÿè¡Œæ™‚ã«åŒã˜ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’å–å¾—ã—ãªã„ã‚ˆã†ã«ï¼‰
if (updates.length > 0) {
  const lastUpdateId = updates[updates.length - 1].update_id;

  // æ¬¡å›ã®getUpdatesãƒªã‚¯ã‚¨ã‚¹ãƒˆç”¨ã«offsetã‚’ä¿å­˜
  // n8nã®é™çš„ãƒ‡ãƒ¼ã‚¿ã«ä¿å­˜ï¼ˆãƒ¯ãƒ¼ã‚¯ãƒ•ãƒ­ãƒ¼å†…ã§æ°¸ç¶šåŒ–ï¼‰
  $execution.customData.set('last_update_id', lastUpdateId + 1);
}

return processedItems;

