const response = $input.first().json;

if (!response.ok || !response.result || response.result.length === 0) {
  return [];
}

const updates = response.result;
const processedItems = [];
const allowedChatId = parseInt(process.env.TELEGRAM_CHAT_ID);

for (const update of updates) {
  const message = update.message;

  if (message && message.document) {
    const chatId = message.chat.id;

    if (chatId === allowedChatId) {
      const document = message.document;
      const fileName = document.file_name;
      const fileExtension = fileName.split('.').pop().toLowerCase();

      // Excelãƒ•ã‚¡ã‚¤ãƒ«ã®ã¿å‡¦ç†ï¼ˆxlsx, xlsï¼‰
      if (['xlsx', 'xls'].includes(fileExtension)) {
        // ãƒ•ã‚¡ã‚¤ãƒ«åãƒ‘ã‚¿ãƒ¼ãƒ³ç¢ºèª: yyyymmddCROSSROPPONGI.xxx
        const dateMatch = fileName.match(/(\d{4})(\d{2})(\d{2})CROSSROPPONGI/);

        if (dateMatch) {
          const year = dateMatch[1];
          const month = dateMatch[2];
          const day = dateMatch[3];
          const reportDate = `${year}-${month}-${day}`;

          processedItems.push({
            json: {
              update_id: update.update_id,
              message_id: message.message_id,
              chat_id: chatId,
              chat_name: message.chat.title,
              file_id: document.file_id,
              file_name: fileName,
              file_type: fileExtension,
              file_size: document.file_size,
              mime_type: document.mime_type,
              from_user: message.from.first_name || message.from.username,
              from_user_id: message.from.id,
              from_username: message.from.username || '',
              report_date: reportDate,
              received_at: new Date(message.date * 1000).toISOString()
            }
          });
        }
      }
    }
  }
}

// offsetã‚’æ›´æ–°
if (updates.length > 0) {
  const lastUpdateId = updates[updates.length - 1].update_id;
  $execution.customData.set('last_update_id', lastUpdateId + 1);
}

return processedItems;

// parse_sales_excel.jsã®ã‚³ãƒ¼ãƒ‰ã‚’ã“ã“ã«åŸ‹ã‚è¾¼ã¿
// ã¾ãŸã¯å¤–éƒ¨ã‚¹ã‚¯ãƒªãƒ—ãƒˆã¨ã—ã¦èª­ã¿è¾¼ã¿

const fileName = $('Filter Excel Files').item.json.file_name;

const dateMatch = fileName.match(/(\d{4})(\d{2})(\d{2})/);

if (!dateMatch) {
  throw new Error(`ãƒ•ã‚¡ã‚¤ãƒ«åã‹ã‚‰æ—¥ä»˜ã‚’æŠ½å‡ºã§ãã¾ã›ã‚“ã§ã—ãŸ: ${fileName}`);
}

const year = dateMatch[1];
const month = dateMatch[2];
const day = dateMatch[3];
const targetSheetName = String(parseInt(day));

console.log(`å‡¦ç†å¯¾è±¡: ${year}-${month}-${day}, ã‚·ãƒ¼ãƒˆå: "${targetSheetName}"`);

const XLSX = require('xlsx');
const binaryData = $input.first().binary.data;
const workbook = XLSX.read(binaryData);

const availableSheets = workbook.SheetNames;
console.log('åˆ©ç”¨å¯èƒ½ãªã‚·ãƒ¼ãƒˆ:', availableSheets.join(', '));

if (!availableSheets.includes(targetSheetName)) {
  throw new Error(
    `ã‚·ãƒ¼ãƒˆ "${targetSheetName}" ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“ã€‚` +
    `åˆ©ç”¨å¯èƒ½ãªã‚·ãƒ¼ãƒˆ: ${availableSheets.join(', ')}`
  );
}

const worksheet = workbook.Sheets[targetSheetName];

function getCellValue(cellAddress) {
  const cell = worksheet[cellAddress];
  if (!cell) return null;
  return cell.v;
}

function getRangeValue(rangeAddress) {
  const range = XLSX.utils.decode_range(rangeAddress);
  const values = [];

  for (let row = range.s.r; row <= range.e.r; row++) {
    for (let col = range.s.c; col <= range.e.c; col++) {
      const cellAddress = XLSX.utils.encode_cell({ r: row, c: col });
      const value = getCellValue(cellAddress);
      if (value !== null && value !== undefined && value !== '') {
        values.push(value);
      }
    }
  }

  return values.length > 0 ? values[0] : null;
}

function getRangeArray(rangeAddress) {
  const range = XLSX.utils.decode_range(rangeAddress);
  const values = [];

  for (let row = range.s.r; row <= range.e.r; row++) {
    for (let col = range.s.c; col <= range.e.c; col++) {
      const cellAddress = XLSX.utils.encode_cell({ r: row, c: col });
      const value = getCellValue(cellAddress);
      if (value !== null && value !== undefined && value !== '') {
        values.push(value);
      }
    }
  }

  return values;
}

function parseCurrency(value) {
  if (value === null || value === undefined) return null;
  if (typeof value === 'number') return value;
  if (typeof value === 'string') {
    const cleaned = value.replace(/[Â¥,]/g, '');
    const parsed = parseFloat(cleaned);
    return isNaN(parsed) ? null : parsed;
  }
  return null;
}

const extractedData = {
  source_file: fileName,
  sheet_name: targetSheetName,
  extracted_at: new Date().toISOString(),
  business_date: getRangeValue('P2:R2'),
  total_customer_count: getCellValue('O2'),
  male_count: getCellValue('T2'),
  female_count: getCellValue('U2'),
  total_sales: parseCurrency(getRangeValue('K64:M65')),
  cash_shortage: parseCurrency(getRangeValue('O64:O65')),
  section_sales: {
    front: parseCurrency(getCellValue('F5')),
    cloak_supplies: parseCurrency(getCellValue('F10')),
    locker: parseCurrency(getCellValue('F14')),
    bar1: parseCurrency(getCellValue('F15')),
    bar2: parseCurrency(getCellValue('F16')),
    bar3: parseCurrency(getCellValue('F17')),
    bar4: parseCurrency(getCellValue('F18')),
    vip1: parseCurrency(getCellValue('F32')),
    vvip: parseCurrency(getCellValue('F33')),
    party: parseCurrency(getCellValue('F48'))
  },
  receivables: {
    uncollected: parseCurrency(getRangeValue('J61:K61')),
    collected: parseCurrency(getRangeValue('Q61:R61'))
  },
  vip_details: {
    vip_customers: [],
    vvip_customers: []
  }
};

const vipNames = getRangeArray('AA5:AA27');
const vipAmounts = getRangeArray('AB5:AB27');

for (let i = 0; i < Math.min(vipNames.length, vipAmounts.length); i++) {
  if (vipNames[i] && vipAmounts[i]) {
    extractedData.vip_details.vip_customers.push({
      name: vipNames[i],
      amount: parseCurrency(vipAmounts[i])
    });
  }
}

const vvipNames = getRangeArray('AA29:AA52');
const vvipAmounts = getRangeArray('AB29:AB52');

for (let i = 0; i < Math.min(vvipNames.length, vvipAmounts.length); i++) {
  if (vvipNames[i] && vvipAmounts[i]) {
    extractedData.vip_details.vvip_customers.push({
      name: vvipNames[i],
      amount: parseCurrency(vvipAmounts[i])
    });
  }
}

if (typeof extractedData.business_date === 'number') {
  const date = XLSX.SSF.parse_date_code(extractedData.business_date);
  extractedData.business_date = `${date.y}-${String(date.m).padStart(2, '0')}-${String(date.d).padStart(2, '0')}`;
}

if (!extractedData.business_date) {
  extractedData.business_date = `${year}-${month}-${day}`;
}

console.log('ãƒ‡ãƒ¼ã‚¿æŠ½å‡ºå®Œäº†:', JSON.stringify(extractedData, null, 2));

return [{
  json: extractedData
}];

